// Read from body if webhook puts payload there, otherwise from root
const src = $json.body ?? $json;

const lang = (src.language ?? 'en').toLowerCase();
const skills = Array.isArray(src.skills) ? src.skills.filter(Boolean) : [];
const expIn  = Array.isArray(src.experience) ? src.experience : [];

// normalize experience
const exp = expIn.map(e => ({
  title:   (e.title   ?? '').trim(),
  company: (e.company ?? '').trim(),
  dates:   (e.dates   ?? '').trim(),
  bullets: Array.isArray(e.bullets)
    ? e.bullets.map(b => String(b).trim()).filter(Boolean)
    : String(e.desc ?? '')
        .split('\n')
        .map(s => s.trim())
        .filter(Boolean)
}));

// if nothing useful
if (skills.length === 0 && exp.length === 0) {
  return [{
    json: {
      summary: lang === 'ar'
        ? "⚠️ يرجى إضافة المهارات أو الخبرة أولاً."
        : "⚠️ Please add skills or experience first."
    }
  }];
}

// build parts
const skillsText = skills.join(', ');
const expLines = exp
  .map(e => {
    const head = [e.title, e.company, e.dates].filter(Boolean).join(' • ');
    const blts = e.bullets.length ? ' — ' + e.bullets.slice(0,3).join(' | ') : '';
    return head + blts;
  })
  .filter(Boolean);

// summary
let summary;
if (lang === 'ar') {
  if (expLines.length) {
    summary =
      `‎ملخص مهني: خبرة في ${expLines.join('؛ ')}. ` +
      (skills.length ? `متمكن من: ${skillsText}. ` : '') +
      `أركز على تحسين الأداء، جودة التسليم، ونتائج قابلة للقياس.`;
  } else {
    summary =
      `ملخص مهني: مهارات قوية في ${skillsText}. ` +
      `أسعى لتطبيقها على مشاريع عملية وتحقيق قيمة واضحة.`;
  }
} else {
  if (expLines.length) {
    summary =
      `Professional summary: experience in ${expLines.join('; ')}. ` +
      (skills.length ? `Skilled in ${skillsText}. ` : '') +
      `Focused on quality delivery, performance, and measurable outcomes.`;
  } else {
    summary =
      `Professional summary: strong skills in ${skillsText}. ` +
      `Seeking to apply them on real projects with clear business impact.`;
  }
}

// return as a single item with {json:{summary}}
return [{ json: { summary } }];
